import React, { useState } from "react";
import FsLightbox from "fslightbox-react";
import cabins from '../cabins.json';

function Minigallery() {
  const [toggler, setToggler] = useState(false);
  const uonquen = cabins.filter(c => c.name === "Uonquen")[0];
  const imageUrls = uonquen.images.map(i => i.url);

  return (
		<>
			<button onClick={() => setToggler(!toggler)}>
				Open the lightbox.
			</button>
			<FsLightbox
				toggler={toggler}
				sources={imageUrls}
			/>
      <div className="test">
        <img src="./img/cabanas/uonquen/uonquen1.webp" alt="CabaÃ±a Uonquen" />
      </div>
		</>
	);
}

