import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Lazy, Pagination } from 'swiper';
import 'swiper/swiper-bundle.css';
import slide1 from '../img/slider/slide1.webp';
import slide2 from '../img/slider/slide2.webp';
import slide3 from '../img/slider/slide3.webp';
import slide4 from '../img/slider/slide4.webp';


function Slider() {
  const swiperRef = React.useRef(null);

  React.useEffect(() => {
    if (swiperRef.current && !swiperRef.current.initialized) {
      swiperRef.current.initialize();
    }
  }, [swiperRef]);

  return (
    <Swiper
      id="mySwiper"
      lazy={{ loadPrevNext: true, loadPrevNextAmount: 1 }}
      pagination={{ clickable: true }}
      slidesPerView={1}
      speed={1500}
      loop={true}
      cssMode={true}
    >
      <SwiperSlide lazy><img src={slide1} alt="Slider 1" loading="lazy"/></SwiperSlide>
      <SwiperSlide lazy><img src={slide2} alt="Slider 2" loading="lazy"/></SwiperSlide>
      <SwiperSlide lazy><img src={slide3} alt="Slider 3" loading="lazy"/></SwiperSlide>
      <SwiperSlide lazy><img src={slide4} alt="Slider 4" loading="lazy"/></SwiperSlide>
    </Swiper>
  )
}

export default Slider;
