import React, { useState } from "react";
import FsLightbox from "fslightbox-react";
import cabins from '../cabins.json';
import thumb from '../img/cabanas/thumb/uonquen2.jpg';

function Minigallery() {
  const [toggler, setToggler] = useState(false);
  const uonquen = cabins.filter(c => c.name === "Uonquen")[0];
  const imageUrls = uonquen.images.map(i => i.url);

  return (
		<>
			<div>
        {uonquen.images.map((t, index) => (
            <div key={index} onClick={() => setToggler(index)}>
              <img src={t.thumb} alt={`Cabaña ${uonquen.name} thumbnail ${index}`} />
            </div>
          ))}

			</div>
			<FsLightbox
				toggler={toggler}
				sources={imageUrls}
			/>
      <div className="test">

      </div>
		</>
	);
}

export default Minigallery
